/*Full Menu Click*/

.menu_bars,
.menu_bars span,
.full-nav,
.full-nav .full-nav-list li>a {
    -webkit-transition: 300ms linear all;
    -o-transition: 300ms linear all;
    transition: 300ms linear all;
}

.menu_bars {
    padding: 5px;
    width: 38px;
    cursor: pointer;
    display: table;
    position: absolute;
    z-index: 5002;
    right: 50px;
}

.menu_bars.right {
    right: 30px;
}

.menu_bars.left {
    left: 30px;
    right: auto;
}

.navbar-toggle-visible .menu_bars {
    top: 30px;
}

.navbar-toggle-visible .menu_bars.right {
    right: 10px;
}

.navbar-toggle-visible .menu_bars.left {
    left: 10px;
}

.menu_bars.active {
    position: fixed;
    top: 20px;
    height: 36px;
}

.menu_bars.full.active {
    top: auto;
}

.navbar-logo-fade .menu_bars.active {
    top: 10px;
}

.navbar-logo-fade.fixed-fade .menu_bars {
    top: 20px;
}

.navbar-logo-fade.fixed-fade .menu_bars.active {
    top: 10px;
}

.navbar-logo-fade .menu_bars {
    position: relative;
    display: inline-table;
    top: 2px;
}

.menu_bars span {
    width: 30px;
    background: #fff;
    height: 1px;
    display: block;
}

.sticky-bottom .menu_bars span,
.fixed-menu .menu_bars span,
.menu_bars.active span {
    background: #333333;
}

.menu_bars.full.active span,
.navbar-logo-fade .menu_bars.active span {
    background: #fff;
}

.navbar-toggle-visible .menu_bars span,
.navbar-logo-fade.fixed-fade .menu_bars span {
    background: #333333;
}

.menu_bars span:nth-child(1),
.menu_bars span:nth-child(3) {
    width: 20px;
    margin: 0 auto;
}

.menu_bars span:nth-child(2) {
    margin: 5px 0;
}

.menu_bars:hover span:nth-child(1),
.menu_bars:hover span:nth-child(3) {
    width: 30px;
    margin: 0 auto;
}

.menu_bars.active span:nth-child(2) {
    display: none;
}

.menu_bars.active span:nth-child(1),
.menu_bars.active span:nth-child(3) {
    width: 30px;
    top: 20px;
    position: absolute;
    left: 5px;
    margin-top: -1px;
}

.menu_bars.active span:nth-child(1) {
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    transform: rotate(45deg);
}

.menu_bars.active span:nth-child(3) {
    -webkit-transform: rotate(-45deg);
    -ms-transform: rotate(-45deg);
    -o-transform: rotate(-45deg);
    transform: rotate(-45deg);
}

/*full menu*/

.full-nav {
    padding: 60px 30px;
    width: 100vw;
    height: 100vh;
    top: 0;
    left: 0;
    z-index: 2000;
    position: fixed;
    overflow: hidden;
    overflow-y: auto;
    opacity: 0;
    background: rgba(0, 0, 0, .9);
    text-align: center;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
    -moz-box-orient: vertical;
    -moz-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    visibility: hidden;
}

.full-nav>.menu_bars.full {
    position: absolute;
    top: 30px;
    right: 30px;
}

.full-nav.full-nav-open {
    width: 100vw;
    opacity: 1;
    visibility: visible;
}

.full-nav .full-nav-list {
    margin: 40px 0;
    width: 100%;
}

.full-nav .full-nav-list li {
    display: block;
    width: 100%;
    opacity: 0;
    -webkit-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
}

.full-nav .full-nav-list li {
    display: inline-block;
    width: auto;
    margin: 5px 10px;
}

.full-nav .full-nav-list li>a {
    display: block;
    font-size: 14px;
    color: #333;
    font-weight: 600;
    margin: 15px 0;
    text-transform: uppercase;
    font-family: 'Montserrat', sans-serif;
}

.full-nav .full-nav-list li>a {
    color: #fff;
    font-size: 20px;
    font-weight: normal;
    padding: 10px 32px;
}

.full-nav .full-nav-list li>a:hover,
.full-nav .full-nav-list li>a:focus {
    background: #11b0cc;
    -webkit-border-radius: 50px;
    border-radius: 50px;
}

.full-nav.full-nav-open .full-nav-list li {
    /* visibility: visible; */
    opacity: 1;
}

.full-nav .full-nav-list li:nth-child(n+1) {
    -webkit-transition-duration: .1s;
    -o-transition-duration: .1s;
    transition-duration: .1s;
    -webkit-transition-delay: .1s;
    -o-transition-delay: .1s;
    transition-delay: .1s;
}

.full-nav .full-nav-list li:nth-child(n+2) {
    -webkit-transition-duration: .15s;
    -o-transition-duration: .15s;
    transition-duration: .15s;
    -webkit-transition-delay: .15s;
    -o-transition-delay: .15s;
    transition-delay: .15s;
}

.full-nav .full-nav-list li:nth-child(n+3) {
    -webkit-transition-duration: .2s;
    -o-transition-duration: .2s;
    transition-duration: .2s;
    -webkit-transition-delay: .2s;
    -o-transition-delay: .2s;
    transition-delay: .2s;
}

.full-nav .full-nav-list li:nth-child(n+4) {
    -webkit-transition-duration: .25s;
    -o-transition-duration: .25s;
    transition-duration: .25s;
    -webkit-transition-delay: .25s;
    -o-transition-delay: .25s;
    transition-delay: .25s;
}

.full-nav .full-nav-list li:nth-child(n+5) {
    -webkit-transition-duration: .3s;
    -o-transition-duration: .3s;
    transition-duration: .3s;
    -webkit-transition-delay: .3s;
    -o-transition-delay: .3s;
    transition-delay: .3s;
}

.full-nav .full-nav-list li:nth-child(n+6) {
    -webkit-transition-duration: .35s;
    -o-transition-duration: .35s;
    transition-duration: .35s;
    -webkit-transition-delay: .35s;
    -o-transition-delay: .35s;
    transition-delay: .35s;
}

.full-nav .full-nav-list li:nth-child(n+7) {
    -webkit-transition-duration: .4s;
    -o-transition-duration: .4s;
    transition-duration: .4s;
    -webkit-transition-delay: .4s;
    -o-transition-delay: .4s;
