/*-----Slider-----*/

/* Body Sections Styling Start */

.slider-bg-outer:after {
    position: absolute;
    content: "";
    background: rgba(0, 0, 0, 0.5);
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
}

.index-four .slider-caption-outer {
