    bottom: 0;
    margin-top: 70px;
}

.logo-static {
    position: relative;
    display: inline-block;
    max-width: 170px;
}

.main-slider-navigation {
    width: 400px;
    height: 90px;
    position: absolute;
    bottom: 100px;
    left: 50%;
    z-index: 100;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    -webkit-transform: translateX(-50%);
    -ms-transform: translateX(-50%);
    -o-transform: translateX(-50%);
    transform: translateX(-50%);
}

.main-slider-navigation .main-next,
.main-slider-navigation .main-prev {
    background: transparent;
    font-size: 26px;
    color: #FFFFFF;
    position: relative;
    display: inline-block;
    height: auto;
    width: auto;
}

.main-slider-directions {
    right: 40px;
    position: absolute;
    height: 100%;
    margin-top: -7px;
}

.main-slider-directions .swiper-button-next {
    right: 0;
}

.navigation-first-half {
    position: relative;
    width: 50%;
    height: 100%;
    float: left;
}

.main-slider-preview {
    width: 50%;
    height: 90px;
    position: absolute;
    right: 0;
    top: 0;
    overflow: hidden;
    -webkit-border-top-right-radius: 10px;
    border-top-right-radius: 10px;
    -webkit-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
    cursor: pointer;
}

.main-slider-preview div {
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    display: block;
    -webkit-border-top-right-radius: 10px;
    border-top-right-radius: 10px;
    -webkit-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
    -webkit-background-size: cover !important;
    background-size: cover !important;
    background-position: center !important;
    -webkit-transition: all linear 300ms;
    -o-transition: all linear 300ms;
    transition: all linear 300ms;
    -webkit-transform: scale(0);
    -ms-transform: scale(0);
    -o-transform: scale(0);
    transform: scale(0);
}

.main-slider-preview div.active {
    -webkit-transform: scale(1);
    -ms-transform: scale(1);
    -o-transform: scale(1);
    transform: scale(1);
}

.main-slider-preview div.active:hover {
    -webkit-transform: scale(1.2);
    -ms-transform: scale(1.2);
    -o-transform: scale(1.2);
    transform: scale(1.2);
}

.main-slider-pagination {
    left: 20px;
    right: auto;
    text-align: left;
    top: 50%;
    -webkit-transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    -o-transform: translateY(-50%);
    transform: translateY(-50%);
}

.main-slider-text {
    font-size: 60px !important;
