}

.tparrows {
    opacity: .3 !important;
    -webkit-transition: all .3s ease;
    -o-transition: all .3s ease;
    transition: all .3s ease;
}

.tparrows:hover {
    opacity: 1 !important;
}

.hesperiden.tparrows,
.hesperiden.tparrows:hover {
    background: none;
}

