    font-size: 30px;
}

.hesperiden.tparrows.tp-rightarrow:before {
    content: '\e649';
}

.hesperiden.tparrows.tp-leftarrow:before {
    content: '\e64a';
}

.main-slider-section-inner-three .swiper-slide .carousel-text-rotate {
    opacity: 0 !important;
