/*custom bullet & arrow style*/

.tparrows {
    -webkit-border-radius: 50%;
    border-radius: 50%;
    opacity: 1 !important;
    -webkit-transition: all 300ms ease;
    -o-transition: all 300ms ease;
    transition: all 300ms ease;
}

.tparrows,
.tp-bullet {
    background: #ff1637;
}

.tp-bullet {
    -webkit-border-radius: 50%;
    border-radius: 50%;
